<div class="body">	<h1 class="title"><?=$h1?></h1>		<div class="nav">		<div class="fleft"></div>		<div class="fright">			<a style="margin-left:5px;" class="button orange" data-form-apply="">Применить</a>		</div>	</div>		<form id="form" action="<?=$path?>" method="POST" enctype="multipart/form-data">		<table class="table-1" data-scroll="head">			<thead>				<td class="small"></td>				<td class="small">BackGround</td>				<td class="small">Image</td>				<td>H1</td>				<td class="small">					<a class="link_add" data-banner="add" title="добавить"></a>				</td>			</thead>			<tbody data-sortable="body" data-banner="items">			<?php foreach ($banners as $k):?>				<tr data-banner="item">					<td>						<span class="icon-reorder handler" data-sortable="handler"></span>						<input type="hidden" data-sortable="id" name="banner_id[update][<?=$k->id?>]"  value="<?=$k->id?>">						<input type="hidden" data-sortable="order" name="banner_order[update][<?=$k->id?>]" value="<?=$k->order?>">					</td>					<td>						<div class="FM-image-box">							<div class="i">								<img class="FM-image" src="<?=htmlspecialchars($k->cache_bg)?>" alt="">							</div>							<input type="hidden" name="background[update][<?=$k->id?>]" value="<?=htmlspecialchars($k->background)?>">							<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>						</div>					</td>					<td>						<div class="FM-image-box">							<div class="i">								<img class="FM-image" src="<?=htmlspecialchars($k->cache)?>" alt="">							</div>							<input type="hidden" name="image[update][<?=$k->id?>]" value="<?=htmlspecialchars($k->image)?>">							<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>						</div>					</td>					<td>					<?php foreach ($settings->language as $l):?>					<?php $d = $k->description[$l->id];?>						<div class="inf-lang">							<textarea class="inf" name="description[update][<?=$k->id?>][<?=$l->id?>][h1]"><?=$d->h1?></textarea>							<img src="<?=$l->image?>" alt="<?=$l->code?>" title="<?=$l->name?>">						</div>					<?php endforeach;?>					</td>					<td>						<a class="link_del" data-banner="delete" title="удалить"></a>					</td>				</tr>				<?php endforeach;?>			</tbody>		</table>				<input type="hidden" name="edit" value="">	</form>	</div><!-- END BODY --><script> // APPLY$(document).on('click', '[data-form-apply]', function(e){	e.preventDefault();	$(document.body).append('<div class="load"></div>');		if (tinyMCE && tinyMCE.editors){		$(tinyMCE.editors).each(function(){			$(this.getElement()).html(this.getContent());		});	}		AP.init('', $('#form')[0], function(){			location.reload(true);	});	return false;	});</script><script> // BANNER;$(function(){	var _id_rand = 0;		var B = {		create:function(){			var html =			'<tr data-banner="item">'+				'<td>'+					'<span class="icon-reorder handler" data-sortable="handler"></span>'+					'<input type="hidden" data-sortable="id" name="banner_id[insert]['+_id_rand+']"  value="0">'+					'<input type="hidden" data-sortable="order" name="banner_order[insert]['+_id_rand+']" value="0">'+				'</td>'+				'<td>'+					'<div class="FM-image-box">'+						'<div class="i">'+							'<img class="FM-image" src="/img/i/loading_mini.gif" alt="">'+						'</div>'+						'<input type="hidden" name="background[update]['+_id_rand+']" value="">'+						'<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>'+					'</div>'+				'</td>'+				'<td>'+					'<div class="FM-image-box">'+						'<div class="i">'+							'<img class="FM-image" src="/img/i/loading_mini.gif" alt="">'+						'</div>'+						'<input type="hidden" name="image[insert]['+_id_rand+']" value="">'+						'<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>'+					'</div>'+				'</td>'+				'<td>';				$.each(B.lang, function(){					html +=					'<div class="inf-lang">'+						'<textarea class="inf" name="description[insert]['+_id_rand+']['+this.id+'][h1]"></textarea>'+						'<img src="'+this.image+'" alt="'+this.code+'" title="'+this.name+'">'+					'</div>';				});				html +=				'</td>'+				'<td>'+					'<a class="link_del" data-banner="delete" title="удалить"></a>'+				'</td>'+			'</tr>';							html = $(html);			$('[data-banner="items"]').prepend(html).trigger('sortupdate');			setTimeout(function(){html.removeClass('new-tr')},20);						_id_rand++;		},		init:function(lang){			try{				B.lang = $.parseJSON(lang);			}catch(e){				alert('Ошибка');				return;			}			$(document).on('click','[data-banner="add"]', B.create)			.on('click','[data-banner="delete"]',function(){				$(this).parents('[data-banner="item"]').hide(200,function(){					$(this).remove();					//if ( ! $('[data-banner="item"]').length) B.create();				});			});			//if ( ! $('[data-banner="item"]').length) B.create();		}	}		B.init('<?=isset($settings->language) ? json_encode($settings->language) : 0;?>');});</script>