v>											<input type="hidden" name="images[update][<?=$k->id?>]" value="<?=htmlspecialchars($k->image)?>">											<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>										</div>									</td>									<td>										<?php foreach ($settings->language as $l):?>										<?php $d = $k->description[$l->id];?>											<div class="inf-lang">												<input class="inf" type="text" name="images_description[update][<?=$k->id?>][<?=$l->id?>][name]" value="<?=htmlspecialchars($d->name)?>">												<img src="<?=$l->image?>" alt="<?=$l->code?>" title="<?=$l->name?>">											</div>										<?php endforeach;?>									</td>									<td>										<a class="link_del" data-images="delete" title="удалить"></a>									</td>								</tr>							<?php endforeach;?>							</tbody>						</table>					</td>				</tr>			</table>		</div>				<input type="hidden" name="id" value="<?=$material->id?>">		<input type="hidden" name="edit" value="">	</form>	<?php endif;?>	</div><!-- END BODY --><script> // APPLY$(document).on('click', '[data-form-apply]', function(e){	e.preventDefault();	$(document.body).append('<div class="load"></div>');		if (tinyMCE && tinyMCE.editors){		$(tinyMCE.editors).each(function(){			$(this.getElement()).html(this.getContent());		});	}		AP.init('', $('#form')[0], function(){			location.reload(true);	});	return false;	});</script><script> // IMAGES;$(function(){	var _id_rand = 0;		var I = {		create:function(){			var html = 			'<tr data-images="item" class="new-tr">'+				'<td>'+					'<span class="icon-reorder handler" data-sortable="handler"></span>'+					'<input type="hidden" data-sortable="order" name="images_order[insert]['+_id_rand+']" value="0">'+				'</td>'+				'<td>'+					'<div class="FM-image-box">'+						'<div class="i">'+							'<img class="FM-image" src="/img/i/loading_mini.gif" alt="">'+						'</div>'+						'<input type="hidden" name="images[insert]['+_id_rand+']" value="">'+						'<br><a href="/" class="FM-overview">обзор</a> | <a href="/" class="FM-clear">очистить</a>'+					'</div>'+				'</td>'+				'<td>';				$.each(I.lang, function(i){					html +=					'<div class="inf-lang">'+						'<input class="inf" type="text" name="images_description[insert]['+_id_rand+']['+this.id+'][name]" value="">'+						'<img src="'+this.image+'" alt="'+this.code+'" title="'+this.name+'">'+					'</div>';				});				html +=				'</td>'+				'<td>'+					'<a class="link_del" data-images="delete" title="удалить"></a>'+				'</td>'+			'</tr>';							html = $(html);			$('[data-images="items"]').prepend(html).trigger('sortupdate');			setTimeout(function(){html.removeClass('new-tr')},20);						_id_rand++;		},		init:function(lang){			try{				I.lang = $.parseJSON(lang);			}catch(e){				alert('Ошибка');				return;			}			$(document).on('click','[data-images="add"]', I.create)			.on('click','[data-images="delete"]',function(){				$(this).parents('[data-images="item"]').hide(200,function(){					$(this).remove();					if ( ! $('[data-images="item"]').length) I.create();				});			});			if ( ! $('[data-images="item"]').length) I.create();		}	}		I.init('<?=isset($settings->language) ? json_encode($settings->language) : 0;?>');});</script>